import React from "react";
import API from '../utils/API';
import "../styles/styles.css";

class InputForms extends React.Component {
  state = {
    search: '',
    employees: [],
    error: ''
  };

  // When the component mounts, get a list of all available base breeds and update this.state.breeds
  componentDidMount() {
    API.getEmployees()
      .then(res => this.setState({ employees: res.data.message }))
      .catch(err => console.log(err));
  }

  handleInputChange = event => {
    this.setState({ search: event.target.value });
  };

  handleFormSubmit = event => {
    event.preventDefault();
    API.getDogsOfBreed(this.state.search)
      .then(res => {
        if (res.data.status === 'error') {
          throw new Error(res.data.message);
        }
        this.setState({ results: res.data.message, error: '' });
      })
      .catch(err => this.setState({ error: err.message }));
  };

  render() {
    return (
      <div className="form-row">
        <div className="col">
          <form className="form">
            <div className="input-group-prepend">
              <div className="input-group-text">DOB</div>
              <input name="firstYear" type="text" placeholder="01/01/1980" />
              <input name="secondYear" type="text" placeholder="04/12/2020" />
              <input name="name" type="text" placeholder="Search" />
            <button>Submit</button>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default InputForms;
