import React from "react";
import API from "../utils/API";
import "../styles/styles.css";
import Employee from "./Employee";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class InputForms extends React.Component {
  state = {
    employees: [],
    startDate: "FROM",
    endDate: "TO",
    name: ""
  };

  handleStartDate = date => {
    this.setState({
      startDate: date
    });
  };

  handleEndDate = date => {
    this.setState({
      endDate: date
    });
  };

  handleNameSearch = nameSearch => {
    this.setState({
      name: nameSearch
    });
  };

  // When the component mounts, get a list of all available base breeds and update this.state.breeds
  componentDidMount() {
    this.getEmployees();
  }

  getEmployees = () => {
    API.getEmployees()
      .then(res => {
        this.setState({
          employees: res.data.results
        });
        console.log(this.state.employees);
      })
      .catch(err => console.log(err));
  };

  render() {
    return (
      <div>
        <div className="form-row">
          <div className="col">
            <form className="form">
              <div className="input-group-prepend">
                <div className="input-group-text">DOB</div>
                <DatePicker
                  selected={this.state.startDate}
                  onChange={this.handleStartDate}
                  className="yearInput"
                  name="firstYear"
                  type="text"
                  placeholder="01/01/1980"
                />
                <DatePicker
                  selected={this.state.endDate}
                  onChange={this.handleEndDate}
                  className="yearInput"
                  name="secondYear"
                  type="text"
                  placeholder="01/01/1980"
                />
                {/* <input name="firstYear" type="text" placeholder="01/01/1980" /> */}
                {/* <input name="secondYear" type="text" placeholder="04/12/2020" /> */}
                <input
                  selected={this.state.name}
                  onChange={this.handleNameSearch}
                  name="name"
                  type="text"
                  placeholder="Search"
                />
                <button>Submit</button>
              </div>
            </form>
          </div>
        </div>
        <Employee employees={this.state.employees} />
      </div>
    );
  }
}

export default InputForms;
